import React from "react";
import { Row, Col } from "reactstrap";
import AnimateNumber from "react-animated-number";
import Widget from "../../components/Widget";
import { Button } from "@amcharts/amcharts4/core";

class Typography extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      payment: undefined,
      payments: []
    };
    const requestOptions = {
      method: 'POST',
      headers: { 'Content-Type': 'application/json'
      'Authentication': 'Bearer '+localStorage.getItem('token') },
      body: JSON.stringify({ title: 'React POST Request Example' })
    };
    fetch("http://localhost:8090/payment/view", requestOptions).then((response) => response.json()).then((response) => {
      this.setState({ ...this.state, payments: response });
    }
    );
  }
  render() {
    return (<div>
      <h1 className="page-title">
        Envios - <span className="fw-semi-bold">Historial de envios </span>
      </h1>
      <Row>
        <Col xs={12} lg={6}>
          <Widget
            title={
              <h5>
                Envios {" "}
                <small className="text-muted">de lotes</small>
              </h5>
            }
            close
            collapse
          >
            <h4>Reciente {this.state.payments?.length}</h4>
            <p>Lo Ãºltimo enviado <AnimateNumber value={this.state.payments?.length} /></p>
            <div className="widget-padding-md w-100 h-100 text-left border rounded">

              <Row>
                <Col sm={2}>
                  <p >BUYSELL</p>
                </Col>
                <Col sm={2}>
                  <p className="text-info">BUY<span>|</span>SELL</p>
                </Col>
                <Col sm={2}>
                  <p className="text-warning">RATE</p>
                </Col>
                <Col sm={2}>
                  <p className="text-primary">PAGO</p>
                </Col>
                <Col sm={2}>
                  <p className="text-lime">CVN</p>
                </Col>
                <Col sm={2}>
                  <p className="text-success">PYMT</p>
                </Col>
              </Row>
              {this.state.payments.map(p =>
              (
                <Row>
                  <Col sm={2}>
                    <p
                      onClick={(e) => this.view(e, p)}
                      className={`${p.bpyPymtPaymentId ? "text-success" : "text-danger"}`}>{p.bpyCurrencyQty}{p.bpyCurrencyQty2}</p>
                  </Col>
                  <Col sm={2}>
                    <p className="text-info">{p.bpyOrderQty1}<span>|</span>{p.bpyOrderQty2}</p>
                  </Col>
                  <Col sm={2}>
                    <p className="text-warning">{p.bpyCvnAgreedRate}</p>
                  </Col>
                  <Col sm={2}>
                    <p className={`${p.bpyPymtAmount ? "text-primary" : "text-danger"}`}>{p.bpyPymtAmount}</p>
                  </Col>
                  <Col sm={2}>
                    <p className={`${p.bpyCvnConversionId ? "text-lime" : "text-danger"}`}>{p.bpyCvnConversionId}</p>
                  </Col>
                  <Col sm={2}>
                    <p className="text-success">{p.bpyPymtPaymentId}</p>
                  </Col>
                </Row>
              )
              )}
            </div>
          </Widget>
        </Col>
        <Col xs={12} lg={6}>
          <Widget
            title={
              <h5>
                Desembolso con estado<small className="text-muted">{this.state.payment?.bpyStatusId}</small>
              </h5>
            }
            close
            collapse
          >
            <h4>{this.state.payment?.bpyCurrencyQty}{this.state.payment?.bpyCurrencyQty2}</h4>
            <p>Re-enviar</p>
            <div className="widget-padding-md w-100 h-100 text-left border rounded">
              <textarea value={JSON.stringify(this.state.payment, null, 2)} cols="60" rows="55" ></textarea>
            </div>
          </Widget>
        </Col>
      </Row>
    </div>
    )
  }

  view = (e, p) => {
    e.preventDefault();
    this.setState({ payment: p });
  };
}

export default Typography;
